import {reqCartList} from '@/api'

const state = {
    cartList:{}
}
const actions = {
    async getCartList({commit}) {
        let result = await reqCartList()
        if (result.code == 200) {
            commit('CARTLIST', result.data)
        }
    },
}
const mutations = {
    CARTLIST(state,cartList) {
        state.cartList = cartList
    },
}
const getters = {
    cartInfoList(state) {
        return state.cartList[0].cartInfoList||[]
    }
}

export default {
    state,
    actions,
    mutations,
    getters
}
