import {reqSkuDetail} from '@/api'
const state = {
    skuDetail:{}
}
const actions = {
    async skuDetail({commit},skuId) {
        let result = await reqSkuDetail(skuId)
        if (result.code == 200) {
            commit('SKUDETAIL', result.data)
        }
    }

}
const mutations = {
    SKUDETAIL(state,skuDetail) {
        state.skuDetail = skuDetail
    }
}
const getters = {
    categoryView(state) {
        return state.detail.skuDetail.categoryView
    },
    price(state) {
        return state.detail.skuDetail.price
    },
    skuInfo(state) {
        return state.detail.skuDetail.skuInfo
    },
    spuSaleAttrList(state) {
        return state.detail.skuDetail.spuSaleAttrList
    },
}

export default {
    state,
    actions,
    mutations,
    getters
}
