//引入路由
import Home from '@/views/Home'
import Search from '@/views/Search'
import Login from '@/views/Login'
import Register from '@/views/Register'
import Detail from '@/views/Detail'
import AddCartSuccess from '@/views/AddCartSuccess'
import ShopCart from '@/views/ShopCart'
import Trade from '@/views/Trade'

export default [
    // 重定向，在项目跑起来的时候，访问/立马让他定向到首页
    {
        path: "*",
        redirect: "/home"
    },
    {
        name: "home",
        path: "/home",
        component: Home,
        meta: {showFooter:true},
    },
    {
        name: "search",
        path: "/search/:keyword?",
        component: Search,
        meta: {showFooter:true},
        props: true
    },
    {
        name: "login",
        path: "/login",
        component: Login,
        meta: {showFooter:false}
    },
    {
        name: "register",
        path: "/register",
        component: Register,
        meta: {showFooter:false}
    },
    {
        name: "detail",
        path: "/detail/:skuId",
        component: Detail,
    },
    {
        name: "addCartSuccess",
        path: "/addCartSuccess",
        component: AddCartSuccess,
        meta: {showFooter:false}
    },
    {
        name: "shopCart",
        path: "/shopCart",
        component: ShopCart,
    },
    {
        name: "trade",
        path: "/trade",
        component: Trade,
    },
    {
        path: '/trade',
        component: Trade,
        meta: { show: false },
        beforeEnter: (to, from, next) => {
        // ...去交易页面，必须从购物车且购物车列表不能为空
        // if (from.path == '/shopcat') {
        //   console.log(from.path);
        next()
        // } else {
        //   // 其他路由组建来，必须回到原路组件
        //   // console.log(store.state.shopcar.shopCarList.length);
        //   next(false)
        // }
    }
    }, 
    {
        path: '/pay',
        component: Pay,
        meta: { show: false }
    }, 
    {
        path: '/paysuccess',
        component: PaySuccess,
        meta: { show: false }
    }, 
        {
        path: '/center',
        component: Center,
        meta: { show: false },
        children: [
            {
                path: 'myorder',
                component: MyOrder
            }, 
            {
                path: 'groupOrder',
                component: GroupOrder
            }, 
            {
                path: '/center',
                redirect: '/center/myorder'
            }
        ]
    }
]
