import {reqSkuDetail} from '@/api'
const state = {
    skuDetail:{}
}
const actions = {
    async skuDetail({commit},skuId) {
        let result = reqSkuDetail(skuId)
        console.log(result);
        if (result.code == 200) {
            commit('SKUDETAIL', result.data)
        }
    }

}
const mutations = {
    SKUDETAIL(skuDetail) {
        state.skuDetail = skuDetail
    }
}
const getters = {}

export default {
    state,
    actions,
    mutations,
    getters
}
