import { reqCategoryList,reqBannerList } from '@/api'

const state = {
    categoryList:[],
}
const actions = {
    async categoryList({commit}){
        let result = await reqCategoryList();
        if (result.code ==200) {
            commit('CATEGORYLIST', result.data)
        }
    },

    async categoryList({commit}){
        let result = await reqCategoryList();
        if (result.code ==200) {
            commit('CATEGORYLIST', result.data)
        }
    }
}
const mutations = {
    CATEGORYLIST(state, categoryList) {
        state.categoryList = categoryList
    }
}
const getters = {}

export default {
    // ...
    state,
    actions,
    mutations,
    getters
}
