import { reCategoryList } from '@/api'

const state = {
    categoryList:[],
}
const actions = {
    categoryList(commit){
        let result = reCategoryList();
        console.log

        if (result.code ==200) {
            commit('CATEGORYLIST', result.data)
        }
    }
}
const mutations = {
    CATEGORYLIST(state, categoryList) {
        state.categoryList = categoryList
    }
}
const getters = {}

export default {
    // ...
    state,
    actions,
    mutations,
    getters
}
