//引入路由
import Home from '@/views/Home'
import Search from '@/views/Search'
import Login from '@/views/Login'
import Register from '@/views/Register'
import Detail from '@/views/Detail'

export default [
    {
        name: "home",
        path: "/home",
        component: Home,
        meta: {showFooter:true},
    },
    {
        name: "search",
        path: "/search/:keyword?",
        component: Search,
        meta: {showFooter:true},
        props: true
    },
    {
        name: "login",
        path: "/login",
        component: Login,
        meta: {showFooter:false}
    },
    {
        name: "register",
        path: "/register",
        component: Register,
        meta: {showFooter:false}
    },
    // 重定向，在项目跑起来的时候，访问/立马让他定向到首页
    {
        path: "*",
        redirect: "/home"
    },
    {
        name: "detail",
        path: "/detail/:sku",
        component: Datail,
    },
]
