import {reqSkuDetail} from '@/api'
const state = {
    skuDetail:{}
}
const actions = {
    skuDetail({commit},skuId) {
        let result = reqSkuDetail(skuId)
        console.log(skuDetail);
        if (result.code == 200) {
            commit('SKUDETAIL', result.data)
        }
    }

}
const mutations = {
    SKUDETAIL(skuDetail) {
        console.log(skuDetail);
        state.skuDetail = skuDetail
    }
}
const getters = {}

export default {
    state,
    actions,
    mutations,
    getters
}
